import React from 'react';

interface State {
  todo: any;
}

class Form extends React.Component<{},State> {

  public state: State = {
    todo: ''
  };

  public render() {
    return (
      <form>
        <input
          type="text"
          onChange={e => {
            this.setState({
              todo: e.target.value,
            });
          }}
          value={this.state.todo}
        />
        <button>登録</button>
      </form>
    );
  }

  private handleChnage(e: React.FormEvent<HTMLInputElement>) {
    e.preventDefault() //onSubmitのデフォルトの挙動で、submitしたときにページが切り替わるのを防ぐ
    // this.props.onSubmit(this.state.input);
    this.setState({input: ""});
  }

}

export default Form;
